import React, { Component, useEffect, useState } from "react";
import { Routes, Route, useNavigate, Link } from "react-router-dom";
import styles from "./styles.module.css";

// function GoBack() {
//   const navigate = useNavigate();
//   navigate(-1);
// }

class NavBar extends Component {
  render() {
    return (
      <>
        <div>
          <Link to="/homework-4">Home</Link>
          <Link to="/homework-4/movies">Movies</Link>
        </div>
      </>
    );
  }
}

class HomePage extends Component {
  render() {
    const { movies, fetchInfoAboutMovie } = this.props;
    return (
      <>
        <NavBar />
        <ul className={styles.trending}>
          {movies.map((movie) => (
            <li>
              <Link
                className={styles.Link}
                to={"movies/:" + movie.id}
                onClick={() => {
                  fetchInfoAboutMovie(movie.id);
                }}
              >
                {movie.name}
              </Link>
            </li>
          ))}
        </ul>
      </>
    );
  }
}

class MoviesPage extends Component {
  render() {
    const { fetchInfoAboutMovie } = this.props;

    return <></>;
  }
}

function MovieDetailsPage() {
  const [movie, setMovie] = useState({});
  const navigate = useNavigate();

  const getInfoAboutMovie = async () => {
    const { fetchInfoAboutMovie } = this.props;
    setMovie(await fetchInfoAboutMovie());
    //this.setState({ movie });
  };

  useEffect(() => {
    async function test() {
      const { fetchInfoAboutMovie, setCurrentMovie } = this.props;
      let currentMovieFromStorage =
        localStorage.getItem("currentMovie") !== null
          ? JSON.parse(localStorage.getItem("currentMovie"))
          : null;
      const movie = await fetchInfoAboutMovie();
      console.log(JSON.stringify(movie));
      if (
        currentMovieFromStorage !== null &&
        Object.keys(currentMovieFromStorage).length > 0 &&
        ((await JSON.stringify(movie)) !== null) ===
          JSON.stringify(currentMovieFromStorage)
      ) {
        setMovie(currentMovieFromStorage);
        //this.setState({ movie: currentMovieFromStorage });
        //   return;
      }

      setCurrentMovie(movie);
      setMovie(movie);
    }
    test();
    //this.setState({ movie });
  });

  //   async componentDidMount() {

  //   }
  //console.log(this.state.movie);
  if (Object.keys(movie).length > 0)
    return (
      <>
        <NavBar />
        <button onClick={() => navigate(-1)}>Go back</button>
        <img
          src={"https://image.tmdb.org/t/p/original/" + movie.posterImage}
          style={{ height: "300px", float: "left" }}
        />
        <h1>{movie.name}</h1>
        <p>User score: {movie.userScore}%</p>
        <h2>Overview</h2>
        <p>{movie.overview}</p>
        <h2>Genres</h2>
        <p>
          {movie.genres.map((genre) => (
            <span>{genre.name + " "}</span>
          ))}
        </p>
      </>
    );
}

class Cast extends Component {
  render() {
    return <></>;
  }
}

class Reviews extends Component {
  render() {
    return <></>;
  }
}

class App extends Component {
  state = {
    movies: [],
    movieId: 0,
  };

  getTrending = () => {
    return fetch(
      "https://api.themoviedb.org/3/trending/all/day?api_key=2b063754856898754bb8ffd8c55819f3"
    )
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((movies) => {
        console.log(movies);
        return movies.results.map((movie) => {
          if (movie.name !== undefined) return { ...movie, name: movie.name };
          else return { ...movie, name: movie.title };
        });
      });
  };

  updateMovies = async () => {
    const movies = await this.getTrending();
    this.setState({ movies });
  };

  fetchInfoAboutMovie = async (movieId) => {
    if (
      movieId !== this.state.movieId &&
      movieId !== undefined &&
      movieId !== null
    ) {
      await this.setState({ movieId });
    }
    return await fetch(
      `https://api.themoviedb.org/3/movie/${this.state.movieId}?api_key=2b063754856898754bb8ffd8c55819f3&language=en-US`
    )
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((movieDetails) => {
        if (movieDetails.name !== undefined)
          return { ...movieDetails, name: movieDetails.name };
        else return { ...movieDetails, name: movieDetails.title };
      })
      .then((movie) => {
        return {
          posterImage: movie.poster_path,
          name: movie.name,
          userScore: Math.round(movie.vote_average * 10),
          overview: movie.overview,
          genres: movie.genres,
        };
      });
  };

  setCurrentMovie = (currentMovie) => {
    this.setState({ currentMovie });
    localStorage.setItem("currentMovie", JSON.stringify(currentMovie));
  };

  componentDidMount() {
    this.updateMovies();
  }

  render() {
    return (
      <>
        <Routes>
          <Route
            exact
            path="/"
            element={
              <HomePage
                movies={this.state.movies}
                fetchInfoAboutMovie={this.fetchInfoAboutMovie}
              />
            }
          />
          <Route path="movies/" element={<MoviesPage />} />
          <Route
            path={"movies/:" + this.state.movieId}
            element={
              <MovieDetailsPage
                fetchInfoAboutMovie={this.fetchInfoAboutMovie}
                setCurrentMovie={this.setCurrentMovie}
              />
            }
          />
          <Route
            path={"movies/:" + this.state.movieId + "/cast"}
            element={<MovieDetailsPage extraInfo={<Cast />} />}
          />
          <Route
            path={"movies/:" + this.state.movieId + "/reviews"}
            element={<MovieDetailsPage extraInfo={<Reviews />} />}
          />
          {/* <Route path="*" element={<Navigate to="/" replace />} /> */}
        </Routes>
      </>
    );
  }
}

export default App;
