import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import editImage from "./images/Daco_4390267.png";
import trashCan from "./images/Daco_4874208.png";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 2,
    name: "Asus ROG Gaming",
    price: "1000",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 3,
    name: "Amazon Kindle",
    price: "150",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
];

function ModalWindow(props) {
  const { product, changeProducts, setModal } = props;

  const [img, setImg] = useState(product.img);
  const [price, setPrice] = useState(product.price);
  const [name, setName] = useState(product.name);
  const [currency, setCurrency] = useState(product.currency);

  const handleInputChange = ({ target }) => {
    const { value, name } = target;

    switch (name) {
      case "name":
        setName(value);
        break;
      case "value":
        setImg(value);
        break;
      case "price":
        setPrice(value);
        break;
    }
  };

  const submitChanges = (evt) => {
    evt.preventDefault();
    changeProducts({ id: product.id, img, price, name, currency });
    setModal(false);
  };

  return (
    <div className={styles.modal}>
      <form>
        <label>Вставьте ссылку на изображение:</label>
        <br></br>
        <input
          type="text"
          name="name"
          required
          onChange={handleInputChange}
          value={img}
        />
        <br />
        <label>Введите название товара:</label>
        <br></br>
        <input
          type="text"
          name="name"
          required
          onChange={handleInputChange}
          value={name}
        />
        <label>Введите стоимость товара:</label>
        <br></br>
        <input
          type="text"
          name="name"
          required
          onChange={handleInputChange}
          value={price}
        />
        <br></br>
        <button onClick={submitChanges}>Сохранить изменения</button>
      </form>
    </div>
  );
}

function Product(props) {
  const { product, addToCart, changeProduct, deleteProduct } = props;

  return (
    <div className={styles.product__wrapper}>
      <div className={styles.product__buttons__wrapper}>
        <button
          className={styles.blank__button}
          onClick={() => changeProduct(product)}
        >
          <img src={editImage} width="20px"></img>
        </button>
        <button
          className={styles.blank__button}
          onClick={() => deleteProduct(product)}
        >
          <img src={trashCan} width="20px"></img>
        </button>
      </div>
      <figure>
        <div className={styles.product__image}>
          <img src={product.img} width="auto" height="100px" />
        </div>
        <figcaption>
          <div>{product.name}</div>
          <div>
            {product.price}
            {product.currency}
          </div>
        </figcaption>
      </figure>
      <button
        className={styles.addToCart}
        onClick={() => {
          alert("Товар добавлен в корзину");
          addToCart(product);
        }}
      >
        Add to cart
      </button>
    </div>
  );
}

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState(initialState);
  const [product, setProduct] = useState(null);

  function addToCart(product) {
    setCart([...cart, product]);
  }

  function changeProduct(product) {
    setModal(true);
    setProduct(product);
  }

  function changeProducts(new_product) {
    console.log()
    setProducts(
      [...products.filter((elt) => elt.id !== new_product.id), new_product].sort(
         (a, b) => {
          if (a.id > b.id) {
            return 1;
          }
          if (a.id < b.id) {
            return -1;
          }
          return 0;
        }
      )
    );
  }

  function deleteProduct(product) {
    console.log(products.filter((elt) => elt.id !== product.id));
    setProducts(products.filter((elt) => elt.id !== product.id));
  }

  return (
    <>
      <ul className={styles.list}>
        {products.map((elt) => (
          <Product
            key={elt.name}
            product={elt}
            addToCart={addToCart}
            changeProduct={changeProduct}
            deleteProduct={deleteProduct}
          />
        ))}
        <button className={styles.blank__button}>
          <img
            src="https://previews.123rf.com/images/siamimages/siamimages1702/siamimages170202731/72668468-plus-icon-vector-illustration.jpg"
            style={{ height: "100px" }}
          ></img>
        </button>
      </ul>
      {modal && (
        <ModalWindow
          product={product}
          changeProducts={changeProducts}
          setModal={setModal}
        ></ModalWindow>
      )}
    </>
  );
}

export default App;
