import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import editImage from "./images/Daco_4390267.png";
import trashCan from "./images/Daco_4874208.png";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 2,
    name: "Asus ROG Gaming",
    price: "1000",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 3,
    name: "Amazon Kindle",
    price: "150",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
];

function ModalWindow(props) {
  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => (document.body.style.overflow = "unset");
  }, []); // блокировка скролла

  const { product, changeProducts, setModal } = props;
  const [img, setImg] = useState(product.img);
  const [price, setPrice] = useState(product.price);
  const [name, setName] = useState(product.name);
  const [currency, setCurrency] = useState(product.currency);

  const handleInputChange = ({ target }) => { // реагирование на изменение полей и ререндер
    const { value, name } = target;

    switch (name) {
      case "img":
        setImg(value);
        break;
      case "name":
        setName(value);
        break;
      case "price":
        setPrice(value);
        break;
      case "currency":
        setCurrency(value);
        break;
    }
  };

  const submitChanges = (evt) => { // подтверждение введённых данных и закрытие модалки
    evt.preventDefault();
    changeProducts({ id: product.id, img, price, name, currency });
    setModal(false);
  };

  return (
    <>
      <div className={styles.modal}>
        <form>
          <label>Вставьте ссылку на изображение:</label>
          <br></br>
          <input
            type="text"
            name="img"
            required
            onChange={handleInputChange}
            value={img}
          />
          <br />
          <label>Введите название товара:</label>
          <br></br>
          <input
            type="text"
            name="name"
            required
            onChange={handleInputChange}
            value={name}
          />
          <br></br>
          <label>Введите стоимость товара:</label>
          <br></br>
          <input
            type="text"
            name="price"
            required
            onChange={handleInputChange}
            value={price}
          />
          <br></br>
          <button onClick={submitChanges}>Сохранить изменения</button>
        </form>
      </div>
      <div className={styles.background} onClick={() => setModal(false)}></div>
    </>
  );
}



function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState(initialState);
  const [product, setProduct] = useState(null);

  function addToCart(product) { // добавление товара в корзину
    setCart([...cart, product]);
  }

  function changeProduct(product) { // вызов модалки и передача текущих полей товара в неё
    setModal(false);
    setModal(true);
    setProduct(product);
  }

  function changeProducts(new_product) { // добавление нового/изменённого товара в список товаров
    setProducts(
      [
        ...products.filter((elt) => elt.id !== new_product.id),
        new_product,
      ].sort((a, b) => {
        if (a.id > b.id) {
          return 1;
        }
        if (a.id < b.id) {
          return -1;
        }
        return 0;
      })
    );
  }

  function deleteProduct(product) { // удаление товара из списка
    console.log(products.filter((elt) => elt.id !== product.id));
    setProducts(products.filter((elt) => elt.id !== product.id));
  }

  return (
    <>
      <ul className={styles.list}>  {/* рендер списка товаров */}
        {products.map((elt) => (
          <Product
            key={elt.name}
            product={elt}
            addToCart={addToCart}
            changeProduct={changeProduct}
            deleteProduct={deleteProduct}
          />
        ))}
        <button   /*кнопка добавления нового товара */
          className={styles.blank__button}
          onClick={() =>
            changeProduct({
              id: products.length + 1,
              img: null,
              name: null,
              price: null,
              currency: null,
            })
          }
        >
          <img
            src="data:image/png;base64,"
            style={{ height: "100px", paddingInline: "65px" }}
          ></img>
        </button>
      </ul>
      {modal && ( /* рендер модалки изменения/добавления товара */
        <ModalWindow
          product={product}
          changeProducts={changeProducts}
          setModal={setModal}
        ></ModalWindow>
      )}
    </>
  );
}

export default App;
