import { createReducer } from "@reduxjs/toolkit";
import { deleteContact } from "./contactsActions";
import { initialState } from "./phonebook";

getFilteredContacts = () => {
    const contacts = [...this.state.contacts];

    const filteredContacts = contacts.filter((contact) =>
      contact.name.toLowerCase().includes(this.state.filter.toLowerCase())
    );
    return filteredContacts;
  };

  remove = (state, id) => {
    const contacts = [...state.contacts];
    const filteredContacts = contacts.filter((contact) => contact.id !== id);
    localStorage.setItem('contacts', JSON.stringify(filteredContacts));
    //this.setState({ contacts: filteredContacts });
    return filteredContacts;
  };

const reducer = createReducer(initialState, {
  [addContact]: (state, action) => [...state.items, action.payload],
  [deleteContact]: (state, action) => state.contacts.items.filter((contact) => contact.id !== action.payload),
});

export { reducer };
