import { useState } from "react";

export function CartProduct(props) {
  const { product } = props;
  const [amount, setAmount] = useState(product.amount);
  return (
    <div>
      <img src={product.img} height="100px"></img>
      <div>
        <div>{product.name}</div>
        <div>Количество:
            <input
              type="text"
              name="amount"
              required
              onChange={handleInputChange}
              value={amount}
            />
        </div>
      </div>
    </div>
  );
}
