import { createReducer } from "@reduxjs/toolkit";
import { initialState } from "./phonebook";

const reducer = createReducer(initialState, {
  [addC]: (state, action) => [...state.items, action.payload],
  [deleteC]: (state, action) =>
    state.items.filter((elt) => elt.id !== action.payload),
});

export { reducer };
