import styles from "../../styles.module.css";
import { useEffect, useState } from "react";

import {
  addToCart,
  removeFromCart,
  toggleModal,
  setCurrentProduct,
  changeProducts,
  deleteProduct,
} from "../../redux/actions";
import { useDispatch } from "react-redux";

export function ModalEdit(props) {

  const dispatch = useDispatch();
  const { product, changeProducts, setModal } = props;
  const [img, setImg] = useState(product.img);
  const [price, setPrice] = useState(product.price);
  const [name, setName] = useState(product.name);
  const [currency, setCurrency] = useState(product.currency);

    useEffect(() => {
      document.body.style.overflow = "hidden";
      return () => (document.body.style.overflow = "unset");
    }, []); // блокировка скролла
  
    
  
    const handleInputChange = ({ target }) => { // реагирование на изменение полей и ререндер
      const { value, name } = target;
  
      switch (name) {
        case "img":
          setImg(value);
          break;
        case "name":
          setName(value);
          break;
        case "price":
          setPrice(value);
          break;
        case "currency":
          setCurrency(value);
          break;
      }
    };
  
    const submitChanges = (evt) => { // подтверждение введённых данных и закрытие модалки
      evt.preventDefault();
      dispatch(changeProducts({ id: product.id, img, price, name, currency }));

      setModal(false);
    };
  
    return (
      <>
        <div className={styles.modal}>
          <form>
            <label>Вставьте ссылку на изображение:</label>
            <br></br>
            <input
              type="text"
              name="img"
              required
              onChange={handleInputChange}
              value={img}
            />
            <br />
            <label>Введите название товара:</label>
            <br></br>
            <input
              type="text"
              name="name"
              required
              onChange={handleInputChange}
              value={name}
            />
            <br></br>
            <label>Введите стоимость товара:</label>
            <br></br>
            <input
              type="text"
              name="price"
              required
              onChange={handleInputChange}
              value={price}
            />
            <br></br>
            <button onClick={submitChanges}>Сохранить изменения</button>
          </form>
        </div>
        <div className={styles.background} onClick={() => setModal(false)}></div>
      </>
    );
  }
