import React, { Component } from "react";
import { MagnifyingGlass } from "react-loader-spinner";
import styles from "./styles.module.css";

class SearchBar extends Component {
  render() {
    const { search } = this.props;
    return (
      <header className={styles.Searchbar}>
        <form className={styles.SearchForm}>
          <button
            type="submit"
            onClick={search}
            className={styles.SearchForm_button}
          >
            <span className={styles.SearchForm_button_label}>Search</span>
          </button>

          <input
            className={styles.SearchForm_input}
            type="text"
            autoComplete="off"
            autoFocus
            placeholder="Search images and photos"
          />
        </form>
      </header>
    );
  }
}

const ImageGallery = () => {
  return <ul className={styles.ImageGallery}></ul>;
};

const ImageGalleryItem = () => {
  return (
    <li className={styles.ImageGalleryItem}>
      <img className={styles.ImageGalleryItem_image} src="" alt="" />
    </li>
  );
};

const Button = () => {
  return <></>;
};

class App extends Component {
  state = {
    search: "",
    images: [],
  };

  addImages = (data) => {
    console.log(data);
  }

  search = (evt) => {
    evt.preventDefault();
    fetch(
      "https://pixabay.com/api/?q=cat&page=1&key=33532854-e632c90cde5486976c8ef79b6&image_type=photo&orientation=horizontal&per_page=12"
    ).then((response) => {
      if (!response.ok) {
        throw new Error(response.status);
      }
      return response.json();
    })
    .then(data => {
      this.addImages(data);
    })
  };
  render() {
    return (
      <>
        <SearchBar search={this.search} />
        <ImageGallery />
        <Button />

        {/* <MagnifyingGlass
        visible={true}
        height="80"
        width="80"
        ariaLabel="MagnifyingGlass-loading"
        wrapperStyle={{}}
        wrapperclassName="MagnifyingGlass-wrapper"
        glassColor="#c0efff"
        color="#e15b64"
      /> */}
      </>
    );
  }
}

export default App;
