import { useEffect, useState, useRef } from "react";
import styles from "./styles.module.css";
import tennisBall from "./tennis ball.png";
import { memo } from 'react'
import { useDrag } from 'react-dnd'
import { getEmptyImage } from 'react-dnd-html5-backend'
// import { Box } from './Box.js'
// import { ItemTypes } from './ItemTypes.js'
function getStyles(left, top, isDragging) {
  const transform = `translate3d(${left}px, ${top}px, 0)`
  return {
    position: 'absolute',
    transform,
    WebkitTransform: transform,
    // IE fallback: hide the real node using CSS when dragging
    // because IE will ignore our custom "empty image" drag preview.
    opacity: isDragging ? 0 : 1,
    height: isDragging ? 0 : '',
  }
}
export const DraggableBox = memo(function DraggableBox(props) {
  const { id, title, left, top } = props
  const [{ isDragging }, drag, preview] = useDrag(
    () => ({
    //   type: ItemTypes.BOX,
      item: { id, left, top, title },
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }),
    [id, left, top, title],
  )
  useEffect(() => {
    preview(getEmptyImage(), { captureDraggingState: true })
  }, [])
  return (
    <div
      ref={drag}
      style={getStyles(left, top, isDragging)}
      role="DraggableBox"
    >
      {/* <Box title={title} /> */}
      <img
          src={tennisBall}
          ref={ball}
          height="100px"
          style={ballStyle}
          onDrag={dragBall}
          draggable="true"
          //   onMouseDown={() => console.log("DOWN")}
          //   onMouseOver={dragBall}
          onDragStart={setDragEmptyImage}
          onDragEnd={() => {
            console.log("END");
            if (isHit) alert("Hit");
          }}
          //   onDragOver={() => console.log("SUKA")}
          //   onDrop={() => console.log("drop")}
        />
    </div>
  )
})

// const [boxes, setBoxes] = useState()

const boxes = {
    a: { top: 20, left: 80, title: 'Drag me around' },
    b: { top: 180, left: 20, title: 'Drag me too' },
  }

function App() {
  const [ballStyle, setBallStyle] = useState({
    position: "fixed",
    zIndex: 3,
    top: "80vh",
    left: `${Math.random() * 100}vw`,
  });
  const [isDraggable, setIsDraggable] = useState(false);
  const [isHit, setIsHit] = useState(false);
  const [isOverlapping, setIsOverlapping] = useState(false);
  const ball = useRef(null);
  const darts = useRef(null);

  var dragImgEl = document.createElement("span");

  // set its style so it'll be effectively (but not technically) invisible and
  // won't change document flow
  dragImgEl.setAttribute(
    "style",
    "position: absolute; display: block; top: 0; left: 0; width: 0; height: 0;"
  );

  // add it to the document
  document.body.appendChild(dragImgEl);

  //   console.log(dragItem);

  useEffect(
    () =>
      console.log(
        ball.current.getBoundingClientRect(),
        darts.current.getBoundingClientRect()
      ),
    [ball, darts]
  );

  useEffect(() => console.log(isOverlapping), [isOverlapping]);

  const setDragEmptyImage = (e) => {
    console.log(e.target);
    let img = document.createElement("img");
    img.src = "http://kryogenix.org/images/hackergotchi-simpler.png";
    console.log(img);

    // Set it to an un-sourced image:

    e.dataTransfer.setDragImage(document.createElement("img"), 0, 0);
    // e.dataTransfer.setDragImage(dragImgEl, 0, 0);
    // const image = document.createElement("img");
    // image.src = "http://kryogenix.org/images/hackergotchi-simpler.png";
    // // canvas.width = canvas.height = 50;

    // // const ctx = canvas.getContext("2d");
    // // ctx.lineWidth = 4;
    // // ctx.moveTo(0, 0);
    // // ctx.lineTo(50, 50);
    // // ctx.moveTo(0, 50);
    // // ctx.lineTo(50, 0);
    // // ctx.stroke();

    // const dt = event.dataTransfer;
    // dt.setData("text/plain", "Data to Drag");
    // dt.setDragImage(image, 0, 0);
    console.log(e.dataTransfer);

    // event.preventDefault();
    // const dragItem = document.querySelector('img[draggable="true"]');
    // const image = document.createElement("img");
    // image.src = tennisBall;
    // image.height = "50px";
    // event.dataTransfer.setDragImage(null, 0, 0);
    // // console.log()
    // dragItem.dataTransfer.setDragImage(tennisBall, 0, 0);
  };

  const dragBall = (event) => {
    setBallStyle({
      ...ballStyle,
      top: `${event.clientY - 50}px`,
      left: `${event.clientX - 50}px`,
    });
    // console.log(ball.current.getBoundingClientRect())
    if (ball.current && darts.current) {
      const a = ball.current.getBoundingClientRect();
      const b = darts.current.getBoundingClientRect();

      if (
        a.top <= b.top + b.height &&
        a.top + a.height > b.top &&
        a.left <= b.left + b.width &&
        a.left + a.width > b.left
      ) {
        setIsOverlapping(true);
      } else {
        setIsOverlapping(false);
      }
    }
  };

  return (
    <>
      <div className={styles.darts__wrapper}>
        <img
          ref={darts}
          src="https://cdn.pixabay.com/photo/2016/08/23/10/48/dart-board-1614051_1280.png"
          height="350px"
          className={styles.darts}
          onDragEnter={() => setIsHit(true)}
          //   onDrop={() => console.log("TEST")}
          onDragLeave={() => setTimeout(() => setIsHit(false), 1)}
          //   onDragOverCapture={() => console.log("test")}
        />
      </div>

      <div className={styles.line}>
      <DraggableBox {...boxes[0]} />
        
      </div>
    </>
  );
}

export default App;
