import { useState } from "react";
import styles from "./styles.module.css";
import tennisBall from "./tennis ball.png";

function App() {
  const [ballStyle, setBallStyle] = useState({
    position: "fixed",
    zIndex: 3,
    top: "80vh",
    left: `${Math.random() * 100}vw`,
  });

  const dragBall = ({clientX, clientY}) => {
    setBallStyle({...ballStyle, top: `${clientY}px`, left: `${clientX}px`})
  };

  return (
    <>
      <div className={styles.darts__wrapper}>
        <img
          src="https://cdn.pixabay.com/photo/2016/08/23/10/48/dart-board-1614051_1280.png"
          height="350px"
          className={styles.darts}
        />
      </div>
      <div className={styles.line}>
        <img
          src={tennisBall}
          height="100px"
          style={ballStyle}
          onDrag={dragBall}
        />
      </div>
    </>
  );
}

export default App;
