import { createReducer } from "@reduxjs/toolkit";
import { setContact, removeContact, filterContacts } from "./contactsActions";
import { initialState } from "./phonebook";

const contactsReducer = createReducer(
  {
    contacts: {
      items: [],
      filter: "",
    },
  },
  (builder) => {
    builder
      .addCase(setContact, (state, action) => ({
        ...state,
        contacts: {
          ...state.contacts,
          items: state.contacts.items.concat(action.payload),
        },
      }))
      .addCase(removeContact, (state, action) => ({
        ...state,
        contacts: {
          ...state.contacts,
          items: state.contacts.items.filter(
            (contact) => contact.id !== action.payload
          ),
        },
      }))
      .addCase(filterContacts, (state, action) => ({
        ...state,
        filter: action.payload,
      }))
      .addDefaultCase((state, action) => state);
  }
);

export { contactsReducer };
