import { useState } from "react";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "",
  },
];

function ModalWindow(props) {
  const { product } = props;

  const submitChanges = () => {};

  return (
    <>
      <form>
        <label>Вставьте ссылку на изображение:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={product.img}
        />
        <br />
        <label>Введите название товара:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={product.name}
        />
        <label>Введите стоимость товара:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={product.price}
        />
        <button onClick={submitChanges}>Подтвердить</button>
      </form>
    </>
  );
}

// function Button(props) {
//   const { addToCart } = props;
//   return <button onClick={addToCart}>Add to cart</button>;
// }

function Product(props) {
  const { elt, addToCart, setModal, setId } = props;
  return (
    <>
      <button
        onClick={() => {
          setModal(true);
          setId();
        }}
      >
        Изменить товар
      </button>
      <figure>
        <img src={elt.img} width="3em" height="6em" />
        <figcaption>
          <div>{elt.name}</div>
          <div>
            {elt.price}
            {elt.currency}
          </div>
        </figcaption>
      </figure>
      <button onClick={() => addToCart(elt)}>Add to cart</button>
    </>
  );
}

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState(initialState);

  function addToCart(product) {
    setCart([...cart, product]);
  }

  return (
    <>
      <ul>
        {initialState.map((elt) => (
          <Product
            key={elt.name}
            elt={elt}
            addToCart={addToCart}
            setModal={setModal}
          />
        ))}
      </ul>
      {modal && <ModalWindow product={products[0]}></ModalWindow>}
    </>
  );
}

export default App;
