import { useEffect, useState } from "react";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "",
  },
  {
    id: 2,
    name: "Asus ROG Gaming",
    price: "1000",
    currency: "$",
    img: "",
  },
  {
    id: 3,
    name: "Amazon Kindle",
    price: "150",
    currency: "$",
    img: ""
  }
];

function ModalWindow(props) {
  const { product } = props;

  const submitChanges = () => {};

  return (
    <>
      <form>
        <label>Вставьте ссылку на изображение:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={product.img}
        />
        <br />
        <label>Введите название товара:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={product.name}
        />
        <label>Введите стоимость товара:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={product.price}
        />
        <br></br>
        <button onClick={submitChanges}>Подтвердить</button>
      </form>
    </>
  );
}

// function Button(props) {
//   const { addToCart } = props;
//   return <button onClick={addToCart}>Add to cart</button>;
// }

function Product(props) {
  const { product, addToCart, changeProduct } = props;

  const setId = (event) => {
    console.log(event.target);
  };

  return (
    <>
      <button onClick={() => changeProduct(product)}>Изменить товар</button>
      <figure>
        <img src={product.img} width="3em" height="6em" />
        <figcaption>
          <div>{product.name}</div>
          <div>
            {product.price}
            {product.currency}
          </div>
        </figcaption>
      </figure>
      <button onClick={() => addToCart(product)}>Add to cart</button>
    </>
  );
}

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState(initialState);
  const [product, setProduct] = useState(null);

  const setId = (event) => {
    console.log(event);
  };

  useEffect(() => {
    console.log(cart)
  }, [cart]);

  function addToCart(product) {
    setCart([...cart, product]);
  }

  function changeProduct(product) {
    setModal(true);
    setProduct(product);
  }

  function changeProducts (product) {
    setProducts([...products.filter(elt => elt.id !== product.id), product])
  }

  return (
    <>
      <ul>
        {initialState.map((elt) => (
          <Product
            key={elt.name}
            product={elt}
            addToCart={addToCart}
            changeProduct={changeProduct}
            setId={setId}
          />
        ))}
      </ul>
      {modal && <ModalWindow product={product}></ModalWindow>}
    </>
  );
}

export default App;
