import React, { Component, useEffect, useState } from "react";
import {
  Routes,
  Route,
  useNavigate,
  Link,
  Navigate,
  NavLink,
} from "react-router-dom";
import styles from "./styles.module.css";
import { nanoid } from "nanoid";

class SearchBar extends Component {
  state = {
    search: "",
  };
  handleInput = ({ target }) => {
    this.setState({ search: target.value });
  };
  handleSubmit = (evt) => {
    evt.preventDefault();
    const { search } = this.props;
    console.log(this.state.search);
    const dataToSearchFor = this.state.search;
    search(dataToSearchFor);
  };
  render() {
    return (
      <header className={styles.Searchbar}>
        <form className={styles.SearchForm}>
          <button
            type="submit"
            onClick={this.handleSubmit}
            className={styles.SearchForm_button}
          >
            <span className={styles.SearchForm_button_label}>Search</span>
          </button>

          <input
            className={styles.SearchForm_input}
            type="text"
            autoComplete="off"
            autoFocus
            placeholder="Search images and photos"
            onChange={this.handleInput}
          />
        </form>
      </header>
    );
  }
}

class NavBar extends Component {
  render() {
    return (
      <>
        <div>
          <NavLink to="/homework-4">Home</NavLink>
          <NavLink to="/homework-4/movies">Movies</NavLink>
        </div>
      </>
    );
  }
}

function HomePage(props) {
  const { movies, setCurrentMovie } = props;
  const navigate = useNavigate();
  return (
    <>
      <NavBar />
      <button onClick={() => navigate(-1)}>Go back</button>
      <h1>Trending today:</h1>
      <ul className={styles.trending}>
        {movies.map((movie) => (
          <li key={nanoid()}>
            <Link
              className={styles.Link}
              to={"movies/:" + movie.id}
              onClick={() => {
                setCurrentMovie(movie.id);
              }}
            >
              {movie.name}
            </Link>
          </li>
        ))}
      </ul>
    </>
  );
}

function MoviesPage(props) {
  const [movies, setMovies] = useState([]);
  const [keyword, setKeyword] = useState("");
  const navigate = useNavigate();
  const { setCurrentMovieId } = props;

