import { createReducer } from "@reduxjs/toolkit";
import {
  setContact,
  removeContact,
  filterContacts,
  getContacts,
} from "./contactsActions";

const contactsReducer = createReducer(
  {
    contacts: {
      items: [],
      filter: "",
      loading: false,
      error: null,
    },
  },
  (builder) => {
    builder
      .addCase(setContact, (state, action) => ({
        ...state,
        contacts: {
          ...state.contacts,
          items: state.contacts.items.concat(
            action.payload.filter(
              (contact) =>
                !state.contacts.items.some(
                  (preloaded) => preloaded.id === contact.id
                )
            )
          ),
        },
      }))
      .addCase(removeContact, (state, action) => ({
        ...state,
        contacts: {
          ...state.contacts,
          items: action.payload,
        },
      }))
      .addCase(filterContacts, (state, action) => ({
        ...state,
        contacts: {
          ...state.contacts,
          filter: action.payload,
        },
      }))
      .addCase(getContacts.pending, (state) => {
        return { ...state, contacts: { loading: true } };
      })
      .addCase(getContacts.fulfilled, (state, action) => {
        return {
          ...state,
          contacts: {
            ...state.contacts,
            items: action.payload,
            loading: false,
          },
        };
      })
      .addCase(getUsers.rejected, (state, action) => {
        return {
          ...state,
          contacts: {
            ...state.contacts,
            loading: false,
            error: action.error.message,
          },
        };
      })
      .addDefaultCase((state, action) => state);
  }
);

export { contactsReducer };
