import { createReducer } from "@reduxjs/toolkit";
import { setContact, deleteContact, filterContacts } from "./contactsActions";
import { initialState } from "./phonebook";

const contactsReducer = createReducer(
  {
    contacts: {
      items: [],
      filter: "",
    },
  },
  (builder) => {
    builder
      .addCase(setContact, (state, action) => {
        state.contacts.items.concat(action.payload)
      }
      )
      .addCase(deleteContact, (state, action) =>
        state.contacts.items.filter((contact) => contact.id !== action.payload)
      )
      .addCase(
        filterContacts,
        (state, action) =>  ({...state, filter: action.payload})
      )
      .addDefaultCase((state, action) => state);
  }
);

export { contactsReducer };
