import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import { ModalEdit } from "./components/modals/edit";
import Product from "./components/product";
import { useDispatch, useSelector } from "react-redux";
import {
    addToCart,
    removeFromCart,
    toggleModal,
    setCurrentProduct,
    changeProducts,
    deleteProduct,
  } from "./actions";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 2,
    name: "Asus ROG Gaming",
    price: "1000",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 3,
    name: "Amazon Kindle",
    price: "150",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
];

function App() {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.labReducer.products);
  const cart = useSelector((state) => state.labReducer.cart);
  const currentProduct = useSelector((state) => state.labReducer.cart);
  const modalEdit = useSelector((state) => state.labReducer.showEditModal);
  //   console.log(products);
  //   const [modal, setModal] = useState(false);
  //   const [cart, setCart] = useState([]);
  //   const [products, setProducts] = useState(initialState);
  //   const [currentProduct, setCurrentProduct] = useState(null);

//   function addToCart(product) {
//     // добавление товара в корзину
//     dispatch(addToCart(product));
//     // setCart([...cart, product]);
//   }

  function changeProduct(product) {
    // вызов модалки и передача текущих полей товара в неё
    dispatch(toggleModal());
    // setModal(true);
    dispatch(setCurrentProduct(product));
    // setCurrentProduct(product);
  }

  function changeProducts(product) {
    // добавление нового/изменённого товара в список товаров
    dispatch(changeProducts(product));
    // setProducts(
    //   [products.filter((elt) => elt.id !== new_product.id), new_product].sort(
    //     (a, b) => {
    //       if (a.id > b.id) {
    //         return 1;
    //       }
    //       if (a.id < b.id) {
    //         return -1;
    //       }
    //       return 0;
    //     }
    //   )
    // );
  }

//   function deleteProduct(product) {
//     // удаление товара из списка
//     dispatch(deleteProduct(product));
//     // setProducts(products.filter((elt) => elt.id !== product.id));
//   }

  return (
    <>
      <button
        className={styles.addToCart}
        style={{ position: "sticky", top: 10, left: "70vw" }}
      >
        Корзина
      </button>
      <ul className={styles.list}>
        {/* рендер списка товаров */}
        {products.map((elt) => (
          <Product
            key={elt.id}
            product={elt}
            addToCart={addToCart}
            changeProduct={changeProduct}
            deleteProduct={deleteProduct}
          />
        ))}
        <button /*кнопка добавления нового товара */
          className={styles.blank__button}
          onClick={() =>
            changeProduct({
              id: products.length + 1,
              img: "",
              name: "",
              price: "",
              currency: "",
            })
          }
        >
          <img
            src="data:image/png;base64,"
            style={{ height: "100px", paddingInline: "65px" }}
          ></img>
        </button>
      </ul>
      {modalEdit /* рендер модалки изменения/добавления товара */ && (
        <ModalEdit
          product={currentProduct}
          changeProducts={changeProducts}
          setModal={setModal}
        ></ModalEdit>
      )}
    </>
  );
}

export default App;
