import React, { Component, useState, useEffect } from "react";
import styles from "./styles.module.css";
import { nanoid } from "nanoid";
import { addContact, deleteContact, filterContacts } from "./contactsActions";

const initialState = {
  contacts: {
    items: [],
    filter: "",
  },
};

function ContactForm(props) {
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");
  const [shouldUpdate, setShouldUpdate] = useState(false);

  const handleInputChange = ({ target }) => {
    const { value, name } = target;

    switch (name) {
      case "name":
        setName(value);
        break;
      case "number":
        setNumber(value);
        break;
    }
  };

  useEffect(() => {
    setName("");
    setNumber("");
  }, [shouldUpdate]);

  const submitButtonClick = (evt) => {
    evt.preventDefault();
    const { addContact } = props;
    setShouldUpdate(!shouldUpdate);
    addContact({ name: `${name}`, number: number, id: nanoid() });
  };

  return (
    <>
      <form className={styles.name_form}>
        <label>Name</label>
        <br></br>
        <input
          type="text"
          name="name"
          pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
          title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
          required
          onChange={handleInputChange}
        />
        <br></br>
        <label>Number</label>
        <br></br>
        <input
          type="tel"
          name="number"
          pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
          title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
          required
          onChange={handleInputChange}
        />
      </form>
      <button type="submit" onClick={submitButtonClick}>
        Add contact
      </button>
    </>
  );
}

function Filter(props) {
  const filter = ({ target }) => {
    const { changeFilterState } = props;
    const { value } = target;
    changeFilterState(value);
  };

  return (
    <>
      <input
        type="tel"
        name="filter"
        pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
        title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
        required
        onChange={filter}
      />
    </>
  );
}

function ContactList(props) {
  const { contacts, deleteContact } = props;
  return (
    <ul>
      {contacts.map((contact) => (
        <li key={contact.id}>
          {contact.name}: {contact.number}
          <button onClick={() => deleteContact(contact.id)}>Delete</button>
        </li>
      ))}
    </ul>
  );
}

class App extends Component {
  state = {
    contacts: [],
    filter: "",
  };

  componentDidMount() {
    const contacts = JSON.parse(localStorage.getItem("contacts"));
    this.setState({ contacts });
  }

  addContact = (new_contact) => {
    const contacts = [...this.state.contacts];

    if (new_contact.number === "" || new_contact.name === "") return;
    if (contacts.some((contact) => contact.name === new_contact.name)) return;

    contacts.push(new_contact);
    localStorage.setItem("contacts", JSON.stringify(contacts));
    this.setState({ contacts });
  };

  setFilterState = (filter) => {
    this.setState({ filter });
  };

  getFilteredContacts = () => {
    const contacts = [...this.state.contacts];

    const filteredContacts = contacts.filter((contact) =>
      contact.name.toLowerCase().includes(this.state.filter.toLowerCase())
    );
    return filteredContacts;
  };

  deleteContact = (id) => {
    const contacts = [...this.state.contacts];
    const filteredContacts = contacts.filter((contact) => contact.id !== id);
    localStorage.setItem("contacts", JSON.stringify(filteredContacts));
    this.setState({ contacts: filteredContacts });
  };

  render() {
    console.log(this.state);
    return (
      <>
        <h1>Phonebook</h1>
        <ContactForm addContact={this.addContact} />

        <h2>Contacts</h2>
        <Filter changeFilterState={this.setFilterState} />
        <ContactList
          contacts={this.getFilteredContacts()}
          deleteContact={this.deleteContact}
        />
      </>
    );
  }
}

export { initialState };
export default App;
