import { useEffect, useState, useRef } from "react";
import styles from "./styles.module.css";
import tennisBall from "./tennis ball.png";

function App() {
  const [ballStyle, setBallStyle] = useState({
    position: "fixed",
    zIndex: 3,
    top: "80vh",
    left: `${Math.random() * 100}vw`,
  });
  const [draggable, setDraggable] = useState(false);
  const [isHit, setIsHit] = useState(false);
  const ball = useRef(null);
  const darts = useRef(null);

  //   console.log(dragItem);

  useEffect(() => console.log(ball.current, darts.current), [ball, darts]);

  const dragBall = (event) => {
    setBallStyle({
      ...ballStyle,
      top: `${event.clientY - 50}px`,
      left: `${event.clientX - 50}px`,
    });
  };

  return (
    <>
      <div className={styles.darts__wrapper}>
        <img
          ref={darts}
          src="https://cdn.pixabay.com/photo/2016/08/23/10/48/dart-board-1614051_1280.png"
          height="350px"
          className={styles.darts}
          onDragEnter={() => setIsHit(true)}
          //   onDrop={() => console.log("TEST")}
          onDragLeave={() => setTimeout(() => setIsHit(false), 1)}
          //   onDragOverCapture={() => console.log("test")}
        />
      </div>

      <div className={styles.line}>
        <img
          src={tennisBall}
          ref={ball}
          height="100px"
          style={ballStyle}
          onDrag={dragBall}
          draggable="true"
          onDragStart={(event) => {
            const dragItem = document.querySelector('img[draggable="true"]');
            const image = document.createElement("img");
            image.src = tennisBall;
            image.height = "50px";
            event.dataTransfer.setDragImage(image, 0, 0);
            // console.log()
            // dragItem.dataTransfer.setDragImage(tennisBall, 0, 0);
          }}
          onDragEnd={() => {
            console.log("END");
            if (isHit) alert("Hit");
          }}
          //   onDragOver={() => console.log("SUKA")}
          //   onDrop={() => console.log("drop")}
        />
      </div>
    </>
  );
}

export default App;
