import { createReducer } from "@reduxjs/toolkit";
import { addContact, deleteContact } from "./contactsActions";
import { initialState } from "./phonebook";

const reducer = createReducer(
  {
    contacts: {
      items: [],
      filter: "",
    },
  },
  (builder) => {
    builder
      .addCase(addContact, (state, action) => [...state.items, action.payload])
      .addCase(deleteContact, (state, action) =>
        state.contacts.items.filter((contact) => contact.id !== action.payload)
      );
    // [addContact]: (state, action) => [...state.items, action.payload],
    // [deleteContact]: (state, action) =>
    //   state.contacts.items.filter((contact) => contact.id !== action.payload),
  }
);

export { reducer };
