import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import tennisBall from "./tennis ball.png";

function App() {
  const [ballStyle, setBallStyle] = useState({
    position: "fixed",
    zIndex: 3,
    top: "80vh",
    left: `${Math.random() * 100}vw`,
  });
  const [draggable, setDraggable] = useState(false);
  const [isHit, setIsHit] = useState(false);

//   useEffect(() => console.log(isHit), [isHit]);

  const dragBall = (event) => {
    setBallStyle({
      ...ballStyle,
      top: `${event.clientY - 50}px`,
      left: `${event.clientX - 50}px`,
    });
  };

  return (
    <>
      <div className={styles.darts__wrapper}>
        <img
          src="https://cdn.pixabay.com/photo/2016/08/23/10/48/dart-board-1614051_1280.png"
          height="350px"
          className={styles.darts}
          onDragEnter={() => setIsHit(true)}
          //   onDrop={() => console.log("TEST")}
          onDragLeave={() => setTimeout(() => setIsHit(false), 1)}
          //   onDragOverCapture={() => console.log("test")}
        />
      </div>

      <div className={styles.line}>
        <img
          src={tennisBall}
          height="100px"
          style={ballStyle}
          //   onDrag={dragBall}
          draggable="true"
          onDragEnd={() => {
            console.log("END")
            if (isHit) alert("Hit");
          }}
          //   onDragOver={() => console.log("SUKA")}
          //   onDrop={() => console.log("drop")}
        />
      </div>
    </>
  );
}

export default App;
