import { createReducer } from "@reduxjs/toolkit";
import { addContact, deleteContact, filterContacts } from "./contactsActions";
import { initialState } from "./phonebook";

const reducer = createReducer(
  {
    contacts: {
      items: [],
      filter: "",
    },
  },
  (builder) => {
    builder
      .addCase(addContact, (state, action) =>
        state.contacts.items.concat(action.payload)
      )
      .addCase(deleteContact, (state, action) =>
        state.contacts.items.filter((contact) => contact.id !== action.payload)
      )
      .addCase(
        filterContacts,
        (state, action) => (state.contacts.filter === action.payload)
      );
  }
);

export { reducer };
