import { useState } from "react";
import styles from "./styles.module.css";
import tennisBall from "./tennis ball.png";

function App() {
  const [ballStyle, setBallStyle] = useState({
    position: "fixed",
    zIndex: 3,
    top: "80vh",
    left: `${Math.random() * 100}vw`,
  });
  const [draggable, setDraggable] = useState(false);
  const [isHit, setIsHit] = useState(false);

  useState(() => console.log("test"), [isHit]);

  const dragBall = (event) => {
    event.preventDefault();
    setBallStyle({
      ...ballStyle,
      top: `${event.clientY}px`,
      left: `${event.clientX}px`,
    });
  };

  return (
    <>
      <div className={styles.darts__wrapper}>
        <img
          src="https://cdn.pixabay.com/photo/2016/08/23/10/48/dart-board-1614051_1280.png"
          height="350px"
          className={styles.darts}
          onMouseEnter={() => console.log(true)}
          onMouseLeave={() => console.log(false)}
        />
      </div>
      <div className={styles.line}>
        <img
          src={tennisBall}
          height="100px"
          style={ballStyle}
          onDrag={dragBall}
          onDragEnd={() => {
            if (isHit) alert("Попал");
          }}
          //   onMouseEnter={() => setDraggable(true)}
          //   onMouseLeave={() => setDraggable(false)}
        />
      </div>
    </>
  );
}

export default App;
