import React, { Component, useState, useEffect } from "react";
import styles from "./styles.module.css";
import { nanoid } from "nanoid";
import { configureStore } from "@reduxjs/toolkit";

// const store = configureStore({
//   reducer: {
//     addContact,
//     deleteContact,
//     findContact,
//   }
// })

class ContactForm extends Component {
  state = {
    name: "",
    number: "",
  };

  handleInputChange = ({ target }) => {
    const { value, name } = target;
    this.setState({ [name]: value });
  };

  submitButtonClick = (evt) => {
    evt.preventDefault();
    const { addContact } = this.props;
    const { name, number } = this.state;

    addContact({ name: `${name}`, number: number, id: nanoid() });
  };

  render() {
    return (
      <>
        <form className={styles.name_form}>
          <label>Name</label>
          <br></br>
          <input
            type="text"
            name="name"
            pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
            title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
            required
            onChange={this.handleInputChange}
          />
          <br></br>
          <label>Number</label>
          <br></br>
          <input
            type="tel"
            name="number"
            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
            required
            onChange={this.handleInputChange}
          />
        </form>
        <button type="submit" onClick={this.submitButtonClick}>
          Add contact
        </button>
      </>
    );
  }
}

class Filter extends Component {
  filter = ({ target }) => {
    const { changeFilterState } = this.props;
    const { value } = target;
    changeFilterState(value);
  };

  render() {
    return (
      <>
        <input
          type="tel"
          name="filter"
          pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
          title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
          required
          onChange={this.filter}
        />
      </>
    );
  }
}

function ContactList(props) {
  const { contacts, deleteContact } = props;
  return (
    <ul>
      {contacts.map((contact) => (
        <li key={contact.id}>
          {contact.name}: {contact.number}
          <button onClick={() => deleteContact(contact.id)}>Delete</button>
        </li>
      ))}
    </ul>
  );
}

function App() {
  const [contacts, setContacts] = useState([]);
  const [filter, setFilter] = useState("");
  // state = {
  //   contacts: [],
  //   filter: "",
  // };

  useEffect(() => {});

  // componentDidMount() {
  //   const contacts = JSON.parse(localStorage.getItem('contacts'));
  //   this.setState({contacts});
  // }

  function addContact(new_contact) {
    const existingContacts = [...contacts];

    if (new_contact.number === "" || new_contact.name === "") return;
    if (existingContacts.some((contact) => contact.name === new_contact.name))
      return;

    existingContacts.push(new_contact);
    localStorage.setItem("contacts", JSON.stringify(existingContacts));
    setContacts(existingContacts);
    //this.setState({ existingContacts });
  }

  setFilterState = (filter) => {
    setFilter(filter);
    //this.setState({ filter });
  };

  getFilteredContacts = () => {
    const filteredContacts = contacts.filter((contact) =>
      contact.name.toLowerCase().includes(this.state.filter.toLowerCase())
    );
    return filteredContacts;
  };

  deleteContact = (id) => {
    const filteredContacts = contacts.filter((contact) => contact.id !== id);
    localStorage.setItem("contacts", JSON.stringify(filteredContacts));
    setContacts(filteredContacts);
    //this.setState({ contacts: filteredContacts });
  };

  return (
    <>
      <h1>Phonebook</h1>
      <ContactForm addContact={addContact} />

      <h2>Contacts</h2>
      <Filter changeFilterState={setFilterState} />
      <ContactList
        contacts={getFilteredContacts}
        deleteContact={deleteContact}
      />
    </>
  );
}

export default App();
