import { useEffect, useState } from "react";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 2,
    name: "Asus ROG Gaming",
    price: "1000",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 3,
    name: "Amazon Kindle",
    price: "150",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
];

function ModalWindow(props) {
  const { product } = props;

  const handleInputChange = ({target}) => {
    const { value, name } = target;

    switch (name) {
      case "name":
        setName(value);
        break;
      case "value":
        setImg(value);
        break;
    }
  };

  const submitChanges = () => {};

  return (
    <>
      <form>
        <label>Вставьте ссылку на изображение:</label>
        <input
          type="text"
          name="name"
          required
          onChange={handleInputChange}
          value={product.img}
        />
        <br />
        <label>Введите название товара:</label>
        <input
          type="text"
          name="name"
          required
          onChange={handleInputChange}
          value={product.name}
        />
        <label>Введите стоимость товара:</label>
        <input
          type="text"
          name="name"
          required
          onChange={handleInputChange}
          value={product.price}
        />
        <br></br>
        <button onClick={submitChanges}>Сохранить изменения</button>
      </form>
    </>
  );
}

// function Button(props) {
//   const { addToCart } = props;
//   return <button onClick={addToCart}>Add to cart</button>;
// }

function Product(props) {
  const { product, addToCart, changeProduct, deleteProduct } = props;

  return (
    <div>
      <button onClick={() => changeProduct(product)}>Изменить товар</button>
      <button onClick={() => deleteProduct(product)}>Удалить товар</button>
      <figure>
        <img src={product.img} width="auto" height="100px" />
        <figcaption>
          <div>{product.name}</div>
          <div>
            {product.price}
            {product.currency}
          </div>
        </figcaption>
      </figure>
      <button onClick={() => addToCart(product)}>Add to cart</button>
    </div>
  );
}

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState(initialState);
  const [product, setProduct] = useState(null);

  const setId = (event) => {
    console.log(event);
  };

  useEffect(() => {
    console.log(cart);
  }, [cart]);

  function addToCart(product) {
    setCart([...cart, product]);
  }

  function changeProduct(product) {
    setModal(true);
    setProduct(product);
  }

  function changeProducts(product) {
    setProducts([...products.filter((elt) => elt.id !== product.id), product]);
  }

  function deleteProduct(product) {
    console.log(products.filter((elt) => elt.id !== product.id));
    setProducts(products.filter((elt) => elt.id !== product.id));
  }

  return (
    <>
      <ul>
        {products.map((elt) => (
          <Product
            key={elt.name}
            product={elt}
            addToCart={addToCart}
            changeProduct={changeProduct}
            deleteProduct={deleteProduct}
          />
        ))}
      </ul>
      {modal && <ModalWindow product={product}></ModalWindow>}
    </>
  );
}

export default App;
