import { useEffect, useState, useRef } from "react";
import styles from "./styles.module.css";
import tennisBall from "./tennis ball.png";

function App() {
  const [ballStyle, setBallStyle] = useState({
    position: "fixed",
    zIndex: 3,
    top: "80vh",
    left: `${Math.random() * 100}vw`,
  });
  const [isHit, setIsHit] = useState(false);
  const ball = useRef(null);
  const darts = useRef(null);

  let dragImg = new Image(0, 0);
  dragImg.src =
    "data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7";

  const dragBall = (event) => {
    setBallStyle({
      ...ballStyle,
      top: `${event.clientY - 50}px`,
      left: `${event.clientX - 50}px`,
    });
    if (ball.current && darts.current) {
      const a = ball.current.getBoundingClientRect();
      const b = darts.current.getBoundingClientRect();

      if (
        a.top <= b.top + b.height &&
        a.top + a.height > b.top &&
        a.left <= b.left + b.width &&
        a.left + a.width > b.left
      ) {
        setIsHit(true);
      } else {
        setIsHit(false);
      }
    }
  };

  return (
    <>
      <div className={styles.darts__wrapper}>
        <img
          ref={darts}
          src="https://cdn.pixabay.com/photo/2016/08/23/10/48/dart-board-1614051_1280.png"
          height="350px"
          className={styles.darts}
          onDragEnter={() => setIsHit(true)}
          onDragLeave={() => setTimeout(() => setIsHit(false), 1)}
        />
      </div>

      <div className={styles.line}>
        <img
          src={tennisBall}
          ref={ball}
          height="100px"
          style={ballStyle}
          onDrag={dragBall}
          draggable="true"  
          onDragStart={(event) =>  event.dataTransfer.setDragImage(dragImg, 10, 10)}
          onDragEnd={() => {
            if (isHit) alert("Hit");
          }}
        />
      </div>
    </>
  );
}

export default App;
