import React, { Component, useEffect, useState } from "react";
import { Routes, Route, useNavigate, Link, Navigate } from "react-router-dom";
import styles from "./styles.module.css";

class Toggler extends Component {
  state = {
    isOpen: false,
  };

  toggle = () => this.setState((state) => ({ isOpen: !state.isOpen }));

  render() {
    return this.props.children({
      isOpen: this.state.isOpen,
      onToggle: this.toggle,
    });
  }
}

class NavBar extends Component {
  render() {
    return (
      <>
        <div>
          <Link to="/homework-4">Home</Link>
          <Link to="/homework-4/movies">Movies</Link>
        </div>
      </>
    );
  }
}

function HomePage(props) {
  const { movies, fetchInfoAboutMovie, extraInfo } = props;
  const navigate = useNavigate();
  return (
    <>
      <NavBar />
      <button onClick={() => navigate(-1)}>Go back</button>
      <ul className={styles.trending}>
        {movies.map((movie) => (
          <li>
            <Link
              className={styles.Link}
              to={"movies/:" + movie.id}
              onClick={() => {
                fetchInfoAboutMovie(movie.id);
              }}
            >
              {movie.name}
            </Link>
          </li>
        ))}
      </ul>
      <ul>
        {/* <Toggler>
                {({isOpen, onToggle}) => (
                    <button type="button" onClick={onToggle}>
                        {isOpen ? <Cast/> : null}
                    </button>
                )}
            </Toggler> */}
      </ul>
    </>
  );
}

class MoviesPage extends Component {
  render() {
    const { fetchInfoAboutMovie } = this.props;

    return <></>;
  }
}

function MovieDetailsPage(props) {
  const [movie, setMovie] = useState({});
  const [depth, setDepth] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    async function getMovieByFetch() {
      const { fetchInfoAboutMovie } = props;
      setMovie(await fetchInfoAboutMovie());
    }
    getMovieByFetch();
  }, []);

  if (Object.keys(movie).length > 0) {
    const { ExtraInfo } = props;
    return (
      <>
        <NavBar />
        <button onClick={() => navigate(-depth)}>Go back</button>
        <img
          src={"https://image.tmdb.org/t/p/original/" + movie.posterImage}
          style={{ height: "380px" }}
        />
        <h1>{movie.name}</h1>
        <p>User score: {movie.userScore}%</p>
        <h2>Overview</h2>
        <p>{movie.overview}</p>
        <h2>Genres</h2>
        <p>
          {movie.genres.map((genre) => (
            <span>{genre.name + " "}</span>
          ))}
        </p>
        <Link
          onClick={() => setDepth(depth + 1)}
          to={"/homework-4/movies/:" + movie.id + "/cast"}
        >
          Cast
        </Link>
        <br></br>
        <Link
          onClick={() => setDepth(depth + 1)}
          to={"/homework-4/movies/:" + movie.id + "/reviews"}
        >
          Reviews
        </Link>
        {ExtraInfo}
      </>
    );
  }
  return <h1>Failed to load web-page, come back to main menu</h1>;
}

function Cast(props) {
  const [cast, setCast] = useState([]);
  const [id, setId] = useState(0);

  async function fetchCast() {
    return await fetch(
      `https://api.themoviedb.org/3/movie/${id}/credits?api_key=2b063754856898754bb8ffd8c55819f3`
    )
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((credits) =>
        credits.cast
          .filter((actor, index) => index < 3)
          .map((actor) => ({
            character: actor.character,
            picture: actor.profile_path,
            name: actor.name,
          }))
      );
  }

  useEffect(() => {
    async function getMovieCast() {
      const { movieId } = await props;
      setId(movieId);
      if (!cast.length) setCast(await fetchCast());
    }

    getMovieCast();
    console.log(cast);
  });

  return (
    <>
      {cast.map((actor) => (
        <>
          <img
            src={"https://image.tmdb.org/t/p/original/" + actor.picture}
            style={{ height: "225px" }}
          />
          <ul>
            <li>{actor.character}</li>
            <p>Character: {actor.character}</p>
          </ul>
        </>
      ))}
    </>
  );
}

class Reviews extends Component {
  render() {
    return <></>;
  }
}

class App extends Component {
  state = {
    movies: [],
    movieId: 0,
  };

  getTrending = async () => {
    return fetch(
      "https://api.themoviedb.org/3/trending/all/day?api_key=2b063754856898754bb8ffd8c55819f3"
    )
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((movies) => {
        console.log(movies);
        return movies.results.map((movie) => {
          if (movie.name !== undefined) return { ...movie, name: movie.name };
          else return { ...movie, name: movie.title };
        });
      });
  };

  updateMovies = async () => {
    const movies = await this.getTrending();
    this.setState({ movies });
  };

  fetchInfoAboutMovie = async (movieId) => {
    if (
      movieId !== this.state.movieId &&
      movieId !== undefined &&
      movieId !== null
    ) {
      this.setState({ movieId });
    }
    return await fetch(
      `https://api.themoviedb.org/3/movie/${this.state.movieId}?api_key=2b063754856898754bb8ffd8c55819f3&language=en-US`
    )
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((movieDetails) => {
        if (movieDetails.name !== undefined)
          return { ...movieDetails, name: movieDetails.name };
        else return { ...movieDetails, name: movieDetails.title };
      })
      .then((movie) => {
        return {
          posterImage: movie.poster_path,
          name: movie.name,
          id: movie.id,
          userScore: Math.round(movie.vote_average * 10),
          overview: movie.overview,
          genres: movie.genres,
        };
      });
  };

  componentDidMount() {
    this.updateMovies();
  }

  render() {
    return (
      <>
        <Routes>
          <Route
            exact
            path="/"
            element={
              <HomePage
                movies={this.state.movies}
                fetchInfoAboutMovie={this.fetchInfoAboutMovie}
              />
            }
          />
          <Route path="movies/" element={<MoviesPage />} />
          <Route
            path={"movies/:" + this.state.movieId}
            element={
              <MovieDetailsPage
                fetchInfoAboutMovie={this.fetchInfoAboutMovie}
                setCurrentMovie={this.setCurrentMovie}
              />
            }
          />
          <Route
            path={"movies/:" + this.state.movieId + "/cast"}
            element={
              <MovieDetailsPage
                ExtraInfo={<Cast movieId={this.state.movieId} />}
              />
            }
          />
          <Route
            path={"movies/:" + this.state.movieId + "/reviews"}
            element={
              <MovieDetailsPage
                ExtraInfo={<Reviews movieId={this.state.movieId} />}
              />
            }
          />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </>
    );
  }
}

export default App;
