import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import { ModalEdit } from "./components/modals/edit";
import Product from "./components/product";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 2,
    name: "Asus ROG Gaming",
    price: "1000",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
  {
    id: 3,
    name: "Amazon Kindle",
    price: "150",
    currency: "$",
    img: "https://i-center.by/images/o/apple-macbook-pro-13-m2-2022-mneh3_1.jpg",
  },
];

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState(initialState);
  const [product, setProduct] = useState(null);

  function addToCart(product) {
    // добавление товара в корзину
    setCart([...cart, product]);
  }

  function changeProduct(product) {
    // вызов модалки и передача текущих полей товара в неё
    setModal(true);
    setProduct(product);
  }

  function changeProducts(new_product) {
    // добавление нового/изменённого товара в список товаров
    setProducts(
      [
        ...products.filter((elt) => elt.id !== new_product.id),
        new_product,
      ].sort((a, b) => {
        if (a.id > b.id) {
          return 1;
        }
        if (a.id < b.id) {
          return -1;
        }
        return 0;
      })
    );
  }

  function deleteProduct(product) {
    // удаление товара из списка
    console.log(products.filter((elt) => elt.id !== product.id));
    setProducts(products.filter((elt) => elt.id !== product.id));
  }

  return (
    <>
      <ul className={styles.list}>
        {/* рендер списка товаров */}
        {products.map((elt) => (
          <li>
            <Product
              key={elt.name}
              product={elt}
              addToCart={addToCart}
              changeProduct={changeProduct}
              deleteProduct={deleteProduct}
            />
          </li>
        ))}
          <button /*кнопка добавления нового товара */
            className={styles.blank__button}
            onClick={() =>
              changeProduct({
                id: products.length + 1,
                img: null,
                name: null,
                price: null,
                currency: null,
              })
            }
          >
            <img
              src="data:image/png;base64,"
              style={{ height: "100px", paddingInline: "65px" }}
            ></img>
          </button>
      </ul>
      {modal /* рендер модалки изменения/добавления товара */ && (
        <ModalEdit
          product={product}
          changeProducts={changeProducts}
          setModal={setModal}
        ></ModalEdit>
      )}
    </>
  );
}

export default App;
