import React, { Component } from "react";
import { Routes, Route, Navigate, BrowserRouter } from "react-router-dom";

class NavBar extends Component {
  render() {
    return <></>;
  }
}

class HomePage extends Component {
  render() {
    const { movies } = this.props;
    console.log(movies[0]);
    return (
      <>
        <NavBar />
        <ul>
            {movies.map(movie => {
                <li>{movie.name}</li>
            })}
        </ul>
      </>
    );
  }
}

class App extends Component {
  state = {
    movies: [],
  };

  getTrending = () => {
    return fetch(
      "https://api.themoviedb.org/3/trending/all/day?api_key=2b063754856898754bb8ffd8c55819f3"
    )
      .then((response) => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then((movies) => {
        console.log(movies.results);
        return movies.results;
      });
  };

  updateMovies = async () => {
    const movies = await this.getTrending();
    this.setState({ movies });
  };

  componentDidMount() {
    this.updateMovies();
  }

  render() {
    return (
      <>
          <Routes>
            <Route
              exact
              path="/"
              element={<HomePage movies={this.state.movies} />}
            />
            {/* <Route exact path="/movies" element={<MoviesPage />} />
            <Route
              exact
              path={"/movies/:" + movie.id}
              element={<MovieDetailsPage />}
            />
            <Route
              path={"/movies/:" + movie.id + "/cast"}
              element={<MovieDetailsPage extraInfo={<Cast />} />}
            />
            <Route
              path={"/movies/:" + movie.id + "/reviews"}
              element={<MovieDetailsPage extraInfo={<Reviews />} />}
            /> */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
      </>
    );
  }
}

export default App;
//https://api.themoviedb.org/3/movie/550?api_key=2b063754856898754bb8ffd8c55819f3
