import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { toggleCartModal, toggleEditModal } from "../../redux/actions";
import styles from "../../styles.module.css";
import { CartProduct } from "../cartProduct";

export default function CartModal(props) {
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.labReducer.cart);

  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => (document.body.style.overflow = "unset");
  }, []); // блокировка скролла

  return (
    <>
      <div className={styles.modal}>
        {cart.map((product) => (
          <CartProduct product={product}></CartProduct>
        ))}
      </div>
      <button onClick={submitChanges}>Сохранить изменения</button>
      <div
        className={styles.background}
        onClick={() => dispatch(toggleCartModal())}
      ></div>
    </>
  );
}
