import { useState } from "react";

const initialState = [
  {
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "",
  },
];

function modalWindow() {}

function Button() {
    return <button>Add to cart</button>
}

function Product(props) {
  const { elt, addToCart } = props;
  return (
    <>
      <figure>
        <img src={elt.img} width="3em" height="6em" />
        <figcaption>
          <div>{elt.name}</div>
          <div>
            {elt.price}
            {elt.currency}
          </div>
        </figcaption>
      </figure>
      <Button onClick={addToCart}></Button>
    </>
  );
}

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);

  function addToCart(product) {
    setCart([...cart, product]);
  }

  return (
    <>
      <ul>
        {initialState.map((elt) => (
          <Product key={elt.name} elt={elt} addToCart={addToCart} />
        ))}
      </ul>
    </>
  );
}

export default App;
