import { useState } from "react";

const initialState = [
  {
    id: 1,
    name: "Macbook Pro",
    price: "2500",
    currency: "$",
    img: "",
  },
];

function ModalWindow(props) {
  const { img } = props;
  return (
    <>
      <form>
        <label>Вставьте ссылку на изображение:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={img}
        />
        <br />
        <label>Введите название товара:</label>
        <input
          type="text"
          name="name"
          required
          //onChange={handleInputChange}
          value={img}
        />
      </form>
    </>
  );
}

function Button(props) {
  const { addToCart } = props;
  return <button onClick={addToCart}>Add to cart</button>;
}

function Product(props) {
  const { elt, addToCart, setModal } = props;
  return (
    <>
      <button onClick={() => setModal(true)}>Изменить товар</button>
      <figure>
        <img src={elt.img} width="3em" height="6em" />
        <figcaption>
          <div>{elt.name}</div>
          <div>
            {elt.price}
            {elt.currency}
          </div>
        </figcaption>
      </figure>
      <Button addToCart={addToCart}></Button>
    </>
  );
}

function App() {
  const [modal, setModal] = useState(false);
  const [cart, setCart] = useState([]);

  function addToCart(event) {
    console.log("F");
    setCart([...cart, event.target], console.log(cart));
  }

  return (
    <>
      <ul>
        {initialState.map((elt) => (
          <Product
            key={elt.name}
            elt={elt}
            addToCart={addToCart}
            setModal={setModal}
          />
        ))}
      </ul>
      {modal && <ModalWindow img={initialState[0].img}></ModalWindow>}
    </>
  );
}

export default App;
