import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  toggleCartModal,
  toggleEditModal,
  addToCart,
} from "../../redux/actions";
import styles from "../../styles.module.css";
import { CartProduct } from "../cartProduct";

export default function CartModal(props) {
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.labReducer.cart);
  const USD = useSelector((state) => state.labReducer.USD);
  const RUB = useSelector((state) => state.labReducer.RUB);

  useEffect(() => {
    document.body.style.overflow = "hidden";
    return () => (document.body.style.overflow = "unset");
  }, []); // блокировка скролла

  return (
    <>
      <div className={styles.modal}>
        {cart.map((product) => (
          <CartProduct product={product}></CartProduct>
        ))}
        <div>
          Общая стоимость заказа:{" "}
          {cart.reduce(
            (acc, curr) =>
              (acc +=
                curr.price * curr.amount * Math.ceil(curr.currency === "RUB"
                  ? RUB
                  : curr.currency === "BYN"
                  ? 1
                  : USD)),
            0
          )}
          BYN
        </div>
        <button
          onClick={() => {
            alert("Товары успешно заказаны!");
          }}
        >
          Оформить заказ
        </button>
      </div>

      <div
        className={styles.background}
        onClick={() => dispatch(toggleCartModal())}
      ></div>
    </>
  );
}
